mapper.select:
select id,user_name as userName,user_pwd as userPwd
from user
where id=#{userId}

select id,user_name as userName,user_pwd as userPwd
from user

   
      user_name like concat("%",#{userName},"%")
   
   
      user_pwd =#{userPwd}
   

limit #{rowNum},#{pageSize}

select id,user_name as userName,user_pwd as userPwd from user
user_name like concat("%",#{userName},"%")
user_pwd =#{userPwd}
